mapper.select:
select * from (
	select a.*,ROWNUM r from (
		select 
			s.*,
			a.idcard_no,
			a.real_name,
			c.name as cost_name,
			c.descr
		from service s
		inner join account a on a.account_id=s.account_id
		inner join cost c on c.cost_id=s.cost_id


				and s.os_username=#{os_username}


				and s.unix_host=#{unix_host}


				and a.idcard_no=#{idcard_no}


				and s.status=#{status}


		order by s.service_id
	) a
) where r<#{end} and r>#{begin}

select 
	count(*)
from service s
inner join account a on a.account_id=s.account_id
inner join cost c on c.cost_id=s.cost_id


		and s.os_username=#{os_username}


		and s.unix_host=#{unix_host}


		and a.idcard_no=#{idcard_no}


		and s.status=#{status}

mapper.insert:

update service set 
	cost_id=#{cost_id,jdbcType=NUMERIC}
where service_id=#{service_id}
